import * as AT  from 'actionTypes'
import {getRenderedPhonesLength} from 'selectors'
import {
  fetchPhones as fetchPhonesApi,
  loadMorePhones as loadMorePhonesApi,
  fetchPhoneById as fetchPhoneByIdApi,
  fetchCategories as fetchCategoriesApi,
  getUsers as fetchUsersApi,
  createUser as createUserApi,
  loginUser as loginUserApi,
  deleteUser  as  deleteUserApi
} from 'api'

export const fetchPhones = () => async dispatch => {
  dispatch({type: AT.FETCH_PHONES_START})

  try {
    const phones = await fetchPhonesApi();
    dispatch({
      type: AT.FETCH_PHONES_SUCCESS,
      payload: phones
    })
  } catch (err) {
    dispatch({
      type: AT.FETCH_PHONES_FAILURE,
      payload: err,
      error: true
    })
  }
}

export const loadMorePhones = () => async (dispatch, getState) => {
  const offset = getRenderedPhonesLength(getState())

  dispatch({type: AT.LOAD_MORE_PHONES_START})

  try {
    const phones = await loadMorePhonesApi({offset})
    dispatch({
      type: AT.LOAD_MORE_PHONES_SUCCESS,
      payload: phones
    })
  } catch (err) {
    dispatch({
      type: AT.LOAD_MORE_PHONES_FAILURE,
      payload: err,
      error: true
    })
  }
}

