import React from 'react'
import {Link} from 'react-router'
import {connect} from 'react-redux'

import {getTotalBasketCount, getTotalBasketPrice} from 'selectors'

function BasketCart ({totalBasketCount, totalPrice}){
    console.log(totalPrice);
    console.log(totalBasketCount);
    console.log('Quick shop render');
    return(
  <div className='cart'>
    <div className='dropdown'>
      <Link
        to='/basket'
        id='dLabel'
        className='btn btn-inverse btn-block btn-lg'
      >
        <i className='fa fa-fa-shopping-cart' />
        <span>{totalBasketCount} item(s) - ${totalPrice}</span>
      </Link>
    </div>
  </div>
)
}
const mapStateToProps = state => {
    console.log(state);
    console.log('Quick shop');
  return {
    totalBasketCount: getTotalBasketCount(state),
    totalPrice: getTotalBasketPrice(state)
  }
}

export default connect(mapStateToProps, null)(BasketCart)
