import React, {Component} from 'react'
import {connect} from 'react-redux'
import {withRouter} from 'react-router'
import {compose} from 'redux'

import IconButton from 'material-ui/IconButton';


import {withStyles} from "material-ui/styles/index";

import Menu, { MenuItem } from 'material-ui/Menu';
import AccountCircle from 'material-ui-icons/AccountCircle';
import {otherMailFolderListItems} from "components/mainPageLayout/tileData";
import {isUserLogin, toggleNavMenu} from "actions";
import Button from 'material-ui/Button';



const drawerWidth = 180;
const styles = theme => ({
    appBar: {
        width: `calc(100% - ${drawerWidth}px)`,
        position: 'absolute',
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
    },
    root: {
        width: '100%',
    },
    flex: {
        flex: 1,
    },
    menuButton: {
        marginLeft: -12,
        marginRight: 20,
    },
    appBarShift: {
        width: `100% `,
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
    }
});


class HeaderMenu extends  Component {
    constructor (props) {
        super(props);
        this.state = {
            anchor: 'left',
        };
    }


    handleMenu = event => {
        this.setState({ anchorEl: event.currentTarget });
    };
    handleClose = () => {
        this.setState({ anchorEl: null });
    };
    loginUser  = () =>{
        this.props.isUserLogin(true);
    };
    logoutUser = () =>{
        this.props.isUserLogin(false);
        this.setState({ anchorEl: null });

    };
    render() {
        const { anchorEl } = this.state;
        const openL = Boolean(anchorEl);
        const isLogin = this.props.userSession.isLogin;

        if (isLogin){
            return (
                <div >
                    <IconButton
                        aria-owns={openL ? 'menu-appbar' : null}
                        aria-haspopup="true"
                        onClick={this.handleMenu}
                        color="inherit"
                    >
                        <AccountCircle />
                    </IconButton>
                    <Menu
                        id="menu-appbar"
                        anchorEl={anchorEl}
                        anchorOrigin={{
                            vertical: 'top',
                            horizontal: 'right',
                        }}
                        transformOrigin={{
                            vertical: 'top',
                            horizontal: 'right',
                        }}
                        open={openL}
                        onClose={this.handleClose}
                    >
                        <MenuItem onClick={this.loginUser}>Profile</MenuItem>
                        <MenuItem onClick={this.logoutUser}>LogOut</MenuItem>
                    </Menu>
                </div>
            );
        } else {
            return (<Button  onClick={this.loginUser }color="inherit">Login</Button>);
        }

    }
};

const mapStateToProps = (store) => {
    return {
        navMenu: store.navMenu,
        userSession: store.userSession
    }
};

const mapDispatchToProps = {
    toggleNavMenu,
    isUserLogin
};
export default compose(
    withRouter,
    withStyles(styles, { withTheme: true }),
    connect(mapStateToProps, mapDispatchToProps)
)(HeaderMenu)