import {
    USER_SESSION_LOGIN_START,
    USER_SESSION_LOGIN_SUCCESS,
    USER_SESSION_LOGIN_FAILURE,
    USER_LOGIN_START,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAILURE
} from 'actionTypes'

const initialState = {
    user:{},
    isLogin: false
}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case USER_SESSION_LOGIN_START:
            return state;
        case  USER_SESSION_LOGIN_SUCCESS:
            console.log('payload');
            console.log(payload);
            return {
                isLogin: true,
                user: payload
            };
        case USER_LOGIN_SUCCESS:
            return {
                isLogin: true,
                user: payload
            };
        case USER_SESSION_LOGIN_FAILURE:
            return state;
        default:
            return state
    }
}