import {
    FETCH_USER_SESSION_START,
    FETCH_USER_SESSION_SUCCESS,
    FETCH_USER_SESSION_FAILURE,
    USER_LOGIN_START,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAILURE
} from 'actionTypes'

const initialState = {
    user:{},
    isLogin: false
}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_USER_SESSION_START:
            return state;
        case USER_LOGIN_START:
            return state;
        case  FETCH_USER_SESSION_SUCCESS:
            return {
                isLogin: true,
                user: payload
            };
        case USER_LOGIN_SUCCESS:
            return {
                isLogin: true,
                user: payload
            };
        case FETCH_USER_SESSION_FAILURE:
            return state;
        case USER_LOGIN_FAILURE:
            return state;
        default:
            return state
    }
}