
import {
    USER_FETCH_START,
    USER_FETCH_SUCCESS,
    USER_FETCH_FAILURE,
    USER_CREATE_START,
    USER_CREATE_SUCCESS,
    USER_DELETE_SUCCESS,
    USER_DELETE_FAILURE,
    USER_CREATE_FAILURE
} from 'actionTypes'
import R from "ramda";


const initialState = {
    data:{},
    loading:true
};
// const initialState = {
//     loading:true
// };

export default (state = initialState, {type, payload}) => {

    switch (type) {
        case USER_FETCH_START:
            return state;
        case USER_FETCH_SUCCESS:
            return {
                data: payload,
                loading: false
            };
        case USER_CREATE_START:
            return state;
        case USER_CREATE_SUCCESS:
            return {
                data: R.append(payload, state.data ),
                loading: false
            };
        case USER_DELETE_SUCCESS:
            return {
                data:  R.without(R.of(payload), state.data),
                loading: false
            };
        case USER_DELETE_FAILURE:
            console.log('USER_DELETE_FAILURE');
            console.log(state);
            console.log(payload);
            return state;
        case USER_CREATE_FAILURE:
            console.log('USER_CREATE_FAILURE');
            console.log(state);
            console.log(payload);
            return state;
        case USER_FETCH_FAILURE:
            console.log('USER_FAILURE');
            console.log(state);
            console.log(payload);
            return state;
        default:
            return state
    }
}