
import {
    USER_START,
    USER_SUCCESS,
    USER_CREATE_START,
    USER_CREATE_SUCCESS,
    USER_DELETE_SUCCESS
} from 'actionTypes'


const initialState = {}

export default (state = initialState, {type, payload}) => {

    switch (type) {
        case USER_START:
            return state;
        case USER_SUCCESS:
            return payload;
        case USER_CREATE_START:
            return state;
        case USER_CREATE_SUCCESS:
            state.push(payload);
            console.log(state);
            return state ;
        case USER_DELETE_SUCCESS:
            console.log('payload');
            console.log(payload);
            console.log(state);
            return state;
        default:
            return state
    }
}