
import {
    USER_START,
    USER_SUCCESS,
    USER_CREATE_START,
    USER_CREATE_SUCCESS,
    USER_DELETE_SUCCESS
} from 'actionTypes'
import R from "ramda";


const initialState = {
    data:{},
    loading:true
};

export default (state = initialState, {type, payload}) => {

    switch (type) {
        case USER_START:
            return state;
        case USER_SUCCESS:
            return payload;
        case USER_CREATE_START:
            return state;
        case USER_CREATE_SUCCESS:
            return R.append(payload, state);
        case USER_DELETE_SUCCESS:
            return R.without(R.of(payload), state);
        default:
            return state
    }
}